mapper.select:
SELECT id, title, start_date, end_date, img, user_id, like_count
FROM plans
WHERE id = #{id}

mapper.update:
UPDATE plans
SET title = #{title}, start_date = #{start_date}, end_date = #{end_date}, img = #{img}
WHERE id = #{id}

UPDATE plans
SET like_count = like_count+1
WHERE id=#{id}

UPDATE plans
SET like_count = like_count-1
WHERE id=#{id}

mapper.select:
SELECT CNT(*)
FROM plan_likes
WHERE user_id=#{user_id} AND plans_id=#{plans_id}

SELECT id
FROM user
WHERE user_id = #{user_id}
