mapper.select:
select id, title, img, description, place_name, latitude, longitude, like_count
from hotplaces
where user_id = #{userId}

mapper.update:
update hotplaces
set title = #{title}, img = #{img}, description = #{description},
 place_name = #{place_name}, latitude = #{latitude}, longitude = #{longitude}
where id = #{id}

update hotplaces
set like_count = like_count + 1
where id = #{id}

update hotplaces
set like_count = like_count - 1
where id = #{id}
