mapper.select:
select
id, user_id, nickname, profile_img from user
where user_id=#{userId}

select
id, user_id, nickname from user
where id=#{id}

mapper.update:
update user set
nickname=#{nickName}, password=#{password},
email_id=#{emailId},
email_domain=#{emailDomain},
profile_img=#{profileImg}
where user_id=#{userId}

mapper.select:
select
password from user
where user_id=#{userId}

select refresh_token
from refresh_tokens
where user_id = #{userId}

select profile_img
from user
where user_id = #{userId}
